// import { ApolloServer } from "apollo-server-micro";
import { ApolloServer, graphqlExpress } from "apollo-server-express";
require("dotenv/config");
import { typeDefs } from "./typeDefs";
import { resolvers } from "./resolvers";
import connectDb from "../middlewares/dbMiddleware.js";


const apolloServer = new ApolloServer(
  {
    typeDefs,
    resolvers,
  }
);

apolloServer.applyMiddleware((req, res) => ({
  context: {req, res}
}))
export const config = {
  api: {
    bodyParser: false,
  },
};

export default connectDb(apolloServer.createHandler({ path: "/api/graphql" }));
