import { ApolloServer } from "apollo-server-micro";
require("dotenv/config");
import { typeDefs } from "./typeDefs";
import { resolvers } from "./resolvers";
import connectDb from "../middlewares/dbMiddleware.js";
import withCookies from "../middlewares/cookiesMiddleware";
const isAuthenticated = (req) => {
  console.log(req?.cookie);
  const token = req?.cookie["token"];
  if (!token) {
    return false;
  }
  try {
    const verified = jwt.verify(token, process.env.TOKEN_SECRET);
    return verified;
  } catch (err) {
    return false;
  }
};

const context = (ctx) => {
  return {
    cookie: ctx.res.cookie,
    isAuth: isAuthenticated(ctx.req),
  };
};

const apolloServer = new ApolloServer({
  typeDefs,
  resolvers,
  context,
});

export const config = {
  api: {
    bodyParser: false,
  },
};

export default connectDb(
  withCookies(apolloServer.createHandler({ path: "/api/graphql" }))
);
