import React, { useState, useEffect, useRef } from "react";
import LargeCard from "../../components/Cards/LargeCard/LargeCard.js";
import Page from "../../components/Page/Page";
import styles from "../../components/Page/Page.module.css";
import { useParams } from "react-router-dom";
import TextEditor from "../../components/TextEditor/TextEditor";
import { Value } from "slate";
import DatePicker from "react-datepicker";
import { useSelector } from "react-redux";
import "react-datepicker/dist/react-datepicker.css";
import axios from "axios";
const initialValue = {
  document: {
    nodes: [
      {
        object: "block",
        type: "paragraph",
        nodes: [
          {
            object: "text",
            leaves: [
              {
                text: "Edit text and save to post article...",
              },
            ],
          },
        ],
      },
    ],
  },
};

const Edit = (props) => {
  const [textEditorValue, setTextEditorValue] = useState(
    Value.fromJSON(initialValue)
  );
  const [formData, setFormData] = useState({
    title: "To be edited",
    author: "To be edited",
    date: new Date(),
    image:
      "data:image/png;base64,",
  });
  const [article, setArticle] = useState({
    id: {},
    title: "",
    author: "",
    date: new Date(),
    image: "",
  });
  const headerBoxRef = useSelector((state) => state.header.headerBoxRef);
  const { id } = useParams();
  const [toolbarFixed, setToolbarFix] = useState(false);
  const headlineFormRef = useRef();
  useEffect(() => {
    const getArticle = async () => {
      const response = await fetch("/articles?" + id);
      const body = await response.json();
      if (response.status !== 200) throw Error(body.message);

      return body;
    };

    getArticle().then((res) => {
      setArticle({
        id: res._id,
        title: res.title,
        author: res.author,
        date: res.date,
        image: res.image,
      });

      if (res.content) setTextEditorValue(Value.fromJSON(res.content));
    });
  }, []);
  useEffect(() => {
    const height = headerBoxRef ? headerBoxRef.current.clientHeight : 380;
    const fixToolbar = (e) => {
      if (window.scrollY > 2 * height - 66 + 644) {
        setToolbarFix(true);
      } else {
        setToolbarFix(false);
      }
    };

    window.addEventListener("scroll", fixToolbar);
    return () => {
      window.removeEventListener("scroll", fixToolbar);
    };
  }, [toolbarFixed]);

  useEffect(() => {
    setFormData({
      title: article.title,
      author: article.author,
      date: new Date(article.date),
      image: article.image,
    });
  }, [
    article.title,
    article.author,
    article.author,
    article.date,
    article.image,
  ]);

  useEffect(() => {
    const keyDownHandler = (e) => {
      if (e.keyCode === 83 && e.ctrlKey) {
        e.preventDefault();
        saveArticle();
      }
    };

    document.addEventListener("keydown", keyDownHandler);
    return () => {
      document.removeEventListener("keydown", keyDownHandler);
    };
  }, [article, textEditorValue]);
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const saveArticle = () => {
    const editedArticle = { ...article, content: textEditorValue.toJSON() };
    const options = {
      headers: { "Content-Type": "application/json" },
    };

    axios
      .post("/articles/edit", JSON.stringify(editedArticle), options)
      .then((res) => {
        alert("Article saved");
      })
      .catch((err) => {
        if (err.response.status === 401 || err.response.status === 400) {
          alert(err.response.data.error);
        } else if (err.response.status !== 200) {
          throw Error(err);
        }
      });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    setArticle({
      ...article,
      title: formData.title,
      author: formData.author,
      date: formData.date,
      image: formData.image,
    });
    // console.log(article);
    // }
  };

  const onTitleChange = (event) => {
    setFormData({
      ...formData,
      title: event.target.value,
    });
  };

  const onAuthorChange = (event) => {
    setFormData({
      ...formData,
      author: event.target.value,
    });
  };

  const onDateChange = (newDate) => {
    setFormData({
      ...formData,
      date: newDate,
    });
  };

  const onImageChange = (event) => {
    setFormData({
      ...formData,
      image: event.target.value,
    });
  };

  const headline = (
    <div className={styles["headline"]}>
      <LargeCard
        title={article.title}
        author={article.author}
        date={article.date}
        image={article.image}
        onClick={() => {}}
      />
    </div>
  );

  const contents = (
    <div>
      <form
        ref={headlineFormRef}
        onSubmit={handleSubmit}
        className={styles["headline-form"]}
      >
        <div className={styles["horizontal"]}>
          <label>Title: </label>
          <input
            className={styles["headline-form-input"]}
            type="text"
            value={formData.title}
            onChange={onTitleChange}
          />
        </div>
        <div className={styles["horizontal-same-line"]}>
          <div className={styles["horizontal"]}>
            <label>Author: </label>
            <input
              className={`${styles["headline-form-input"]}`}
              type="text"
              value={formData.author}
              onChange={onAuthorChange}
            />
          </div>
          <div className={styles["horizontal"]}>
            <label>Date: </label>
            <DatePicker
              className={`${styles["DatePicker"]}`}
              selected={formData.date}
              onChange={onDateChange}
            />
          </div>
        </div>
        <div className={styles["horizontal"]}>
          <label>Image: </label>
          <input
            className={styles["headline-form-input"]}
            type="text"
            value={formData.image}
            onChange={onImageChange}
          />
        </div>
        <input
          type="submit"
          value="Change Card"
          className={styles["submit-button"]}
        />
      </form>
      <TextEditor
        value={textEditorValue}
        setValue={setTextEditorValue}
        onSave={saveArticle}
        toolbarFixed={toolbarFixed}
        styles={styles}
      />
    </div>
  );

  return (
    <Page
      sidebarFixTopOffset={0}
      headline={headline}
      mainPane={contents}
    />
  );
};

export default Edit;
