import React,{useRef, useEffect} from 'react'
import styles from './VideoPlayer.module.css'
import {
  disableBodyScroll,
  enableBodyScroll,
  clearAllBodyScrollLocks
} from "body-scroll-lock";
import {useSelector} from 'react-redux'
const VideoPlayer = (props, ref) => {
    const scrollLockRef = useRef();
    const isPlaying = useSelector(state => state.videoPlayer.isPlaying)
    useEffect(() => {
      if(isPlaying){
        console.log("Yo start")
        disableBodyScroll(scrollLockRef.current)
      }

      // return () => {
      //   console.log("Yo end")
      //   enableBodyScroll(scrollLockRef.current)
      // }
    }, [isPlaying])


    const selected = useSelector(state=> state.videoPlayer.selected)
    return (
        isPlaying? 
            (<div className={styles["youtube-video-container"]} id="youtube-video-container">
              <div ref={scrollLockRef}>1</div>
              <div className={styles["closeable"]}>
                <div className={styles["close-button"]}>
                  <i className={`${styles["fas"]} ${styles["fa-times"]} fas fa-times`} onClick={() => {props.closeVideo()}}></i>
                  <label className={styles["close-btn-label"]}>Close</label>
                </div>
                <div ref={scrollLockRef}className={styles["video"]}>
                  <iframe
                    src={props.src}
                    frameborder="0"
                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen="true"
                  ></iframe>
                </div>
              </div>
            </div>)
            : null        
    )
};
export default VideoPlayer
