import React,{useState, useEffect} from 'react';
import styles from './LargeCard.module.css';
import MediumCard from '../MediumCard/MediumCard.js'
import Card from '../Card.js'
const LargeCard = (props) =>{
    const [isSmall, setSmall] = useState(false);
    useEffect(() => {
        const updateWindowType = () =>{
            setSmall(window.innerWidth < 960);
        };

        window.addEventListener('resize', updateWindowType);
        return () => {
            window.removeEventListener('resize', updateWindowType);
        }
    });

    return (
        isSmall ? <MediumCard image={props.image} title={props.title} text={props.text} date={props.date} author={props.author}/>:
        <article className={styles.card}>
            <ImageContainer image={props.image} isVideo={props.isVideo}/>
            <CardBody title={props.title} text={props.text} date={props.date} author={props.author}/>
        </article>
    );
}

const ImageContainer = (props) =>{
    const isVideo = (props.LinkType === Card.LinkType["video-external"] || props.LinkType === Card.LinkType["video-youtube"])
    return(
        <div className={styles["img-container"]} style={{backgroundImage: `url('${props.image}')`}}>
            <div>
            {props.isVideo && <i className={`${styles["play-button"]} ${styles["far"]} ${styles["fa-play-circle"]} far fa-play-circle`}></i>}
            </div>
        </div>
    );
}

const CardBody = (props) => {
    return (
        <div className={styles["card-body"]}>
            <h2>{props.title}</h2>
            <p><span className={styles["author"]}>{props.author}</span> | <span className={styles["date"]}>{props.date}</span></p>
            <p className={styles["body-content"]}>{props.text}</p>
        </div>
    )
}

export default LargeCard;
