import React, { useState } from "react";
import styles from "./Comment.module.css";
import { getRules } from "../../components/TextEditor/TextEditor";
import { Card } from "../Cards/Card";
import {Value} from 'slate'
import Html from "slate-html-serializer";
const Comment = (props) => {
  const author = props.author;
  const fullDate = Card.formatDate(props.date);
  const date = fullDate.date;
  const time = fullDate.time;
  const rules = getRules(styles);
  const html = new Html({ rules });
  const regex = RegExp(`<di.*><p>.*</p></di>`);
  const serialized = html.serialize(props.commentText);
  const oneNode = {...props.commentText.toJSON(), document: {...props.commentText.toJSON().document, nodes: props.commentText.toJSON().document.nodes.filter(item, n => n ===0)}}
  const minimized = html.serialize(Value.fromJSON(oneNode));

  console.log(oneNode)

  const [isMinimized, setMinimized] = useState(true);
  return (
    <div className={styles["Comment"]}>
      <div className={styles["card-body"]}>
        {author || date ? (
          <p>
            {author && <span className={styles["author"]}>{author}</span>}
            {date && <span className={styles["date"]}>{date}</span>}
            {time && <span className={styles["time"]}>{time}</span>}
          </p>
        ) : null}
      </div>

      <div
        className={
          isMinimized
            ? `${styles["minimized"]} ${styles["comment-body"]}`
            : styles["comment-body"]
        }
        dangerouslySetInnerHTML={{ __html: minimized}}
      ></div>
    </div>
  );
};

export default Comment;
