import React, {useState} from 'react'
import {Editor} from 'slate-react'
import {Value} from 'slate'

const TextEditor = () => {

    const [value, setValue] = useState("");

    onChange = ({aValue}) => {
        setValue({aValue});
    }

    return (
        <Editor value={value} onChange={onChange}/>
    )

}

export default TextEditor;
