import React from 'react';
import styles from './MediumCard.module.css';

const MediumCard = (props) =>{

    return (
        <article className={styles["card"]}>
            <ImageContainer image={props.image} isVideo={props.isVideo}/>
            <CardBody title={props.title} text={props.text} date={props.date} author={props.author}/>
        </article>
    );
}

const ImageContainer = (props) =>{
    return(
        <div className={styles["img-container"]} style={{backgroundImage: `url('${props.image}')`}}>
            <div>
            {props.isVideo && <i className={`${styles["play-button"]} ${styles["far"]} ${styles["fa-play-circle"]} far fa-play-circle`}></i>}
            </div>
        </div>
    );
}

const CardBody = (props) => {
    return (
        <div className={styles["card-body"]}>
            <h2>{props.title}</h2>
            <p><span className={styles["author"]}>{props.author}</span> | <span className={styles["date"]}>{props.date}</span></p>
            <p className={styles["body-content"]}>{props.text}</p>
        </div>
    )
}

export default MediumCard;
