import React, { useState, useEffect, useRef } from "react";
import LargeCard from "../../components/Cards/LargeCard/LargeCard.js";
import SidePanel from "../../components/SidePanel/SidePanel.js";
import Card from "../../components/Cards/Card.js";
import styles from "./Edit.module.css";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import { useHistory, useParams } from "react-router-dom";
import TextEditor from "../../components/TextEditor/TextEditor";
import { Value } from "slate";
import { isKeyHotkey } from "is-hotkey";
const initialValue = {
  document: {
    nodes: [
      {
        object: "block",
        type: "paragraph",
        nodes: [
          {
            object: "text",
            leaves: [
              {
                text: "This text is editable",
              },
            ],
          },
        ],
      },
    ],
  },
};

const Edit = (props) => {
  const [textEditorValue, setTextEditorValue] = useState(
    Value.fromJSON(initialValue)
  );
  const [formData, setFormData] = useState({
    title: "To be edited",
    author: "To be edited",
    date: "To be edited",
    image: "data:image/png;base64,",
  });
  const [article, setArticle] = useState({
    id: {},
    title: "",
    author: "",
    date: "",
    image: "",
  });
  const { id } = useParams();
  const history = useHistory();
  const [sidePanelFixed, setSidePanelFix] = useState(false);
  const [toolbarFixed, setToolbarFix] = useState(false); 
  const headerBoxRef = useRef();
  const headlineFormRef = useRef();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  useEffect(() => {
    const fixNavbar = (e) => {
      if (window.scrollY > headerBoxRef.current.clientHeight - 66 - 35 + 644) {
        setSidePanelFix(true);
      } else {
        setSidePanelFix(false);
      }
    };

    window.addEventListener("scroll", fixNavbar);
    return () => {
      window.removeEventListener("scroll", fixNavbar);
    };
  }, [sidePanelFixed]);

  useEffect(() => {
    const fixToolbar = (e) => {
      if (window.scrollY > headerBoxRef.current.clientHeight + headlineFormRef.current.clientHeight - 66 + 644) {
        setToolbarFix(true);
      } else {
        setToolbarFix(false);
      }
    };

    window.addEventListener("scroll", fixToolbar);
    return () => {
      window.removeEventListener("scroll", fixToolbar);
    };
  }, [toolbarFixed]);
  useEffect(() => {
    const isSaveHotkey = isKeyHotkey("mod+s");
    const savePressed = (event) => {
      if(isSaveHotkey(event)){
        saveArticle();
      }
    }


    window.addEventListener("keydown", savePressed(event));
    return () => {
      window.removeEventListener("keydown", savePressed(event));
    };
  }, []);

  useEffect(() => {
    console.log(id);
    const getArticle = async () => {
      const response = await fetch("/articles?" + id);
      const body = await response.json();
      if (response.status !== 200) throw Error(body.message);

      return body;
    };

    getArticle().then((res) => {
      setArticle({
        id: res._id,
        title: res.title,
        author: res.author,
        date: res.date,
        image: res.image,
      });

      if (res.content) setTextEditorValue(Value.fromJSON(res.content));
    });
  }, []);

  useEffect(() => {
    console.log("Running this")
    setFormData({
      title: article.title,
      author: article.author,
      date: article.date,
      image: article.image,
    })
  }, [article.title, article.author, article.author, article.date, article.image])



  const saveArticle = async () => {
    const editedArticle = { ...article, content: textEditorValue.toJSON() };

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editedArticle),
    };

    const response = await fetch(`/articles/edit`, requestOptions);
    const data = await response.json();
    if (response.status !== 200) throw Error(data.message);
    alert("Article Saved");
  };

  const getLinkFunction = (linkType) => {
    switch (linkType) {
      case Card.LinkType["video-youtube"]:
        return props.playVideo;
      case Card.LinkType["article-internal"]:
        return (articleLink) => {
          history.push(articleLink);
        };
      default:
        break;
    }
  };



  const renderOnceSidePanel = (
    <SidePanel
      sideBarClicked={props.sideBarClicked}
      setSideBarClicked={props.setSideBarClicked}
      youtubeIds={props.youtubeIds}
      getImageLink={props.getImageLink}
      playVideo={props.playVideo}
      getHyperLink={props.getHyperLink}
      sidePanelFixed={sidePanelFixed}
    />
  );

  const handleSubmit = (event) => {
    event.preventDefault();
    // if(!(article.title === formData.title && article.image === formData.image && article.author === formData.author)){
      console.log(article); 
    setArticle({
        ...article,
        title: formData.title,
        author: formData.author,
        date: formData.date,
        image: formData.image,
      })
      console.log(article);
    // } 
  };

  const onTitleChange = (event) => {
    setFormData({
      ...formData,
      title: event.target.value

    }
    )
  }

  const onAuthorChange = (event) => {
    setFormData({
      ...formData,
      author: event.target.value

    }
    )
  }

  const onDateChange = (event) => {
    setFormData({
      ...formData,
      date: event.target.value,
    }
    )
  }


  const onImageChange = (event) => {
    setFormData({
      ...formData,
      image: event.target.value
    }
    )
  }

  return (
    <div className={styles["overarching"]}>
      <div className={styles["primary-color-background"]}></div>
      <div className={styles["Edit"]}>
        <Header
          ref={headerBoxRef}
          sideBarClicked={props.sideBarClicked}
          setSideBarClicked={props.setSideBarClicked}
          navbarClicked={props.navbarClicked}
          setNavbarClicked={props.setNavbarClicked}
        />
        <div className={styles["headline"]}>
          <LargeCard
            title={article.title}
            author={article.author}
            date={article.date}
            image={article.image}
            onClick={() => {}}
          />
        </div>
        <div className={styles["content-pane"]}>
          <div className={styles["main-pane"]}>
            <form ref={headlineFormRef} onSubmit={handleSubmit} className={styles["headline-form"]}>
              <div className={styles["horizontal"]}><label>Title: <input className={styles["headline-form-input"]} type="text" value={formData.title} onChange={onTitleChange} /></label></div>
              <div className={styles["horizontal"]}>
              <label>Author: <input className={`${styles["headline-form-input"]} ${styles["same-line"]}`} type="text" value={formData.author} onChange={onAuthorChange} /></label>
              <label>Date: <input className={`${styles["headline-form-input"]} ${styles["same-line"]}`} type="text" value={formData.date} onChange={onDateChange} /></label>
              </div>
              <div className={styles["horizontal"]}>
              <label>Image: <input className={styles["headline-form-input"]} type="text" value={formData.image} onChange={onImageChange} /></label>

              </div>
              <input type="submit" value="Change Card" className={styles["submit-button"]}/>

                        </form>
            <TextEditor
              value={textEditorValue}
              setValue={setTextEditorValue}
              onSave={saveArticle}
              toolbarFixed={toolbarFixed}
            />
          </div>

          <div className={styles["side-pane"]}></div>
          {sidePanelFixed ? (
            <div
              style={{ position: "fixed", top: props.topOffset, right: "0px" }}
            >
              {/* <div style={{ position: "fixed", top: "103.5px", right: "0px"}}> */}
              {renderOnceSidePanel}
            </div>
          ) : (
            <div style={{ marginTop: props.topOffset }}>
              {renderOnceSidePanel}
            </div>
          )}
        </div>

        <div className={styles["footer-container"]}>
          <Footer />
        </div>
      </div>
    </div>
  );
};

export default Edit;
