import React from 'react';
import styles from './LargeCard.module.css';

const LargeCard = (props) =>{

    return (
        <article className={styles.card}>
            <ImageContainer image={props.image}/>
            <CardBody title={props.title} text={props.text} date={props.date} author={props.author}/>
        </article>
    );
}

const ImageContainer = (props) =>{
    return(
        <div className="img-container" style={{backgroundImage: `url('${props.image}')`}}>

        </div>
    );
}

const CardBody = (props) => {
    return (
        <div className="card-body">
            <h2>{props.title}</h2>
            <p><span className="author">{props.author}</span> | <span className="date">{props.date}</span></p>
            <p className="body-content">{props.text}</p>
        </div>
    )
}

export default LargeCard;
