import React, {useState, useRef} from "react";
import { Editor } from "slate-react";
import { Value } from "slate";
import styles from './TextEditor.module.css'
import FormatToolbar from "./FormatToolbar"

const initialValue = Value.fromJSON({
  document: {
    nodes: [
      {
        object: "block",
        type: "paragraph",
        nodes: [
          {
            object: "text",
            leaves: [
              {
                text: "My first paragraph",
              },
            ],
          },
        ],
      },
    ],
  },
});

const TextEditor = () => {
  const [value, setValue] = useState(initialValue);

  const ref = useRef();

  const onKeyDown = (e, change, next) => {
    if (!e.ctrlKey) {
      return next()
    }
    e.preventDefault();

    switch (e.key) {
      case "b": {
        change.toggleMark("bold");
        return true;
      }
      case "i": {
        change.toggleMark("italic");
        return true;
      }
      case "`":{
          change.toggleMark("code");
          return true;
      }
      case 'u':{
          change.toggleMark('underline');
          return true;
      }


    }
  };

  const renderMark = (props, editor, next) => {
    switch (props.mark.type) {
      case "bold":
        return <BoldMark {...props} />;
      case "italic":
        return <ItalicMark {...props} />;
      case "code":
          return <code {...props.attributes}>{props.children}</code>
      case "unordered-list":
          return (
              <ul {...props.attributes}>
                  <li>{props.children}</li>
              </ul>
          )
        case "ordered-list":
            return (
                <ol {...props.attributes}>
                    <li>{props.children}</li>
                </ol>
            )
      case "underline":
          return <u {...props.attributes}>{props.children}</u>
      default: 
            return next()
    }
  };

  const onMarkClick = (e, type) => {
      e.preventDefault();
      ref.current.toggleMark(type);;

}




    return (
        <div className={styles["overarching"]}>
        <FormatToolbar>
            <button 
            onPointerDown={(e) => onMarkClick(e, 'bold')}
            className={styles["tooltip-icon-button"]}>
                <i className="fas fa-bold"></i>
            </button>
            <button 
            onPointerDown={(e) => onMarkClick(e, 'italic')}
            className={styles["tooltip-icon-button"]}>
                 <i className="fas fa-bold"></i>
            </button>
            <button 
            onPointerDown={(e) => onMarkClick(e, 'underline')}
            className={styles["tooltip-icon-button"]}>
                <i className="fas fa-bold"></i>
            </button>
            <button 
            onPointerDown={(e) => onMarkClick(e, 'code')}
            className={styles["tooltip-icon-button"]}>
                 <i className="fas fa-bold"></i>
            </button>
            <button 
            onPointerDown={(e) => onMarkClick(e, 'undordered-list')}
            className={styles["tooltip-icon-button"]}>
                 <i className="fas fa-bold"></i>
            </button>

            <button 
            onPointerDown={(e) => onMarkClick(e, 'ordered-list')}
            className={styles["tooltip-icon-button"]}>
                <i className="fas fa-bold"></i>
            </button>

        </FormatToolbar>
        <Editor
        ref={ref}
        className={styles["Editor"]}
        value={value}
        onChange={(opts) => setValue(opts.value)}
        onKeyDown={onKeyDown}
        renderMark={renderMark}
        />
        </div>


    );
  }


const BoldMark = (props) => <strong>{props.children}</strong>;

const ItalicMark = (props) => <em property="italic">{props.children}</em>;

export default TextEditor;
