import React, { Component} from "react";
import { Editor } from "slate-react";
import { Value } from "slate";
import styles from './TextEditor.module.css'
import FormatToolbar from "./FormatToolbar"

import Icon from "react-icons-kit"
import {bold} from "react-icons-kit/feather/bold"
import {italic} from "react-icons-kit/feather/italic"
import Edit from "../../pages/Articles/Edit";


const initialValue = Value.fromJSON({
  document: {
    nodes: [
      {
        object: "block",
        type: "paragraph",
        nodes: [
          {
            object: "text",
            leaves: [
              {
                text: "My first paragraph",
              },
            ],
          },
        ],
      },
    ],
  },
});

export default class TextEditor extends Component {
  state = {
    value: initialValue,
  };

  ref= editor => {this.editor = editor}
  onChange = ({ value }) => {
    this.setState({ value });
  };

  onKeyDown = (e, change) => {
    if (!e.ctrlKey) {
      return;
    }
    e.preventDefault();

    switch (e.key) {
      case "b": {
        change.toggleMark("bold");
        return true;
      }
      case "i": {
        change.toggleMark("italic");
        return true;
      }
    }
  };

  renderMark = (props) => {
    switch (props.mark.type) {
      case "bold":
        return <BoldMark {...props} />;
      case "italic":
        return <ItalicMark {...props} />;
    }
  };

  onMarkClick = (e, type) => {
      e.preventDefault();
    //   const {value} = this.state;
      const change = this.editor.change().toggleMark(type);
      this.onChange(change);

}



  render() {
    return (
        <div className={styles["overarching"]}>
        <FormatToolbar>
            <button 
            onPointerDown={(e) => this.onMarkClick(e, 'bold')}
            className={styles["tooltip-icon-button"]}>
                <Icon icon ={bold}/>
            </button>
            <button 
            onPointerDown={(e) => this.onMarkClick(e, 'italic')}
            className={styles["tooltip-icon-button"]}>
                <Icon icon ={italic}/>
            </button>
        </FormatToolbar>
        <Editor
        ref={this.ref}
        className={styles["Editor"]}
        value={this.state.value}
        onChange={this.onChange}
        onKeyDown={this.onKeyDown}
        renderMark={this.renderMark}
        />
        </div>


    );
  }
}

const BoldMark = (props) => <strong>{props.children}</strong>;

const ItalicMark = (props) => <em property="italic">{props.children}</em>;
