import React from 'react';
import './MediumCard.css';

const CardHeader = (props) => 
    {
        const image = props.image;
        const style = {
            backgroundImage: 'url(' + image  + ')' 
        };
        return(
            <header style={style} 
                id={image}
                className="card-header">
                    <h4 className="card-header--title">News</h4>
                </header>
        );
    }

const Button = () => {
    return (
        <button className="button button-primary">
            <i className="fa fa-chevron-right">Find out more</i>
        </button>
    );
}

const CardBody = (props) => {
    return (
        <div className="card-body">
            <p className="date">{props.date}</p>
    <h2>{props.title}</h2>
    <p className="body-content">{props.text}</p>
    <Button />
        </div>
    );
}

const MediumCard = () => {
    return (
        <article className="card">
            <CardHeader image={'https://source.unsplash.com/user/erondu/600x400'}/>
            <CardBody date={'March 15, 2020'} title={'What happened in Thailand?'} text={'Kayaks crowd Three Sister Springs, where people and manatees maintain controversial coexistence'}/>

        </article>
    );
}

export default MediumCard;
