import React,{useRef, forwardRef, useState, useEffect} from "react";
import styles from "./LargeCard.module.css";
import Card from "../Card.js";
import {useSelector} from 'react-redux'


const LargeCard = (props) => {

  const audioRef= useSelector(state => state.audioPlayer.audioPlayerRef)


  return (



    <article className={styles.card}>
      <ImageContainer
        image={props.image}
        LinkType={props.LinkType}
        onClick={props.onClick}
        link={props.link}
        audioRef={props.audioRef}
      />
      <CardBody
        title={props.title}
        text={props.text}
        date={Card.formatDate(props.date)}
        author={props.author}
        onClick={props.onClick? props.onClick : () => {console.log("")}}
        link={props.link}
        LinkType={props.LinkType}
        audioRef={props.audioRef}
      />
    </article>
  );
};

const ImageContainer =(props) => {
  const isVideo =
  props.LinkType === Card.LinkType["video-external"] ||
  props.LinkType === Card.LinkType["video-youtube"] || 
  props.LinkType === Card.LinkType["audio-internal"];
  return (
    props.image?
    <div
      className={styles["img-container"]}
      onClick={() => {
        if(props.LinkType === Card.LinkType["audio-internal"]){
          props.audioRef.current.play();
        }
        props.onClick(props.link);
      }}
    >
       <img  src={props.image}/>
      <div>

        {isVideo && (
          <i
            className={`${styles["play-button"]} ${styles["far"]} ${styles["fa-play-circle"]} far fa-play-circle`}
          ></i>
        )}

      </div>
    </div>: null
  );
};

const CardBody = (props) => {
  return (
    (props.title) ?
    (<div className={styles["card-body"]}>
      <h2
        onClick={() => {
          if(props.LinkType === Card.LinkType["audio-internal"]){
            props.audioRef.current.play();
          }
          props.onClick(props.link);
        }}
      >
        {props.title}
      </h2>
      <p>
        <span className={styles["author"]}>{props.author}</span>
        <span className={styles["date"]}>{props.date}</span>
      </p>
      {props.text && <p className={styles["body-content"]}>{props.text}</p>}
    </div>) :
    null
  );
};

export default LargeCard;
