import React,{useState} from 'react';
import styles from './SmallCard.module.css';

const SmallCard = (props) =>{
    const [darkness, setDarkness] = useState(false);


    return (
        <article className={styles["card"]}>
            <ImageContainer image={props.image} onMouseEnter={() => {setDarkness(true)}} onMouseExit={() => {setDarkness(false)}}/>
            <CardBody title={props.title} text={props.text} date={props.date} author={props.author}/>
        </article>
    );
}

const ImageContainer = (props) =>{
    return(
        <div className={styles["img-container"]} style={{backgroundImage: `url('${props.image}')`}}>

        </div>
    );
}

const CardBody = (props) => {
    return (
        <div className={styles["card-body"]}>
            <h2>{props.title}</h2>
        </div>
    )
}

export default SmallCard;
