import React,{useEffect, useState} from "react";
import LargeCard from "../../components/Cards/LargeCard/LargeCard.js";
import MediumCard from "../../components/Cards/MediumCard/MediumCard"
import Page from "../../components/Page/Page";
import styles from "../../components/Page/Page.module.css";
import { useSelector } from "react-redux";
const Home = (props) => {
  const articles = useSelector((state) => state.blog.articles);
  const [isMobile, setIsMobile] = useState(false);
  useEffect(() => {
    const updateIsMobile = () => {
      if (window.innerWidth < 550) {
        setIsMobile(true)
      }
    };
    window.addEventListener("resize", updateIsMobile);
    return () => {
      window.addEventListener("resize", updateIsMobile);
    };
  }, []);

  const contents = articles.map((item, index) => (
    index % 4 == 0 || !isMobile? (
      <div className={`${styles["main-pane-item"]} ${styles["main-pane-item-centered"]}`}>
        <LargeCard
          image={item.image}
          date={item.date}
          title={item.title}
          text={item.description}
          author={item.author}
          contentType={item.contentType}
          link={item.link}
        />
      </div>
    ) : (
      <div className={`${styles["main-pane-item"]}`}>
        <MediumCard
          image={item.image}
          date={item.date}
          title={item.title}
          text={item.description}
          author={item.author}
          contentType={item.contentType}
          link={item.link}
        />
      </div>
    )
  ));
  return <Page sidebarFixTopOffset={0} mainPane={contents} />;
};

export default Home;
