import React, { useState } from "react";
import Comment from "./Comment";
import TextEditor from "../TextEditor/TextEditor";
import { Value } from "slate";
import styles from "./Comment.module.css";
const CommentBar = (props) => {
  const initialValue = {
    document: {
      nodes: [
        {
          object: "block",
          type: "paragraph",
          nodes: [
            {
              object: "text",
              leaves: [
                {
                  text: "",
                },
              ],
            },
          ],
        },
      ],
    },
  };

  const [textEditorValue, setTextEditorValue] = useState(
    Value.fromJSON(initialValue)
  );

  const onChange = (newValue) => {
    setTextEditorValue(Value.fromJSON(initialValue.document.nodes.push(newValue)))
  };

  const postComment = () => {
    props.postComment(textEditorValue);
  };

  return (
    <div>
      <h1 className={styles["heading-special"]}>Comments</h1>
      <div
        style={{
          borderStyle: "solid solid solid solid",
          borderColor: "var(--secondary-color)",
        }}
      >
        <TextEditor
          value={textEditorValue}
          // setValue={setTextEditorValue}
          setValue={onChange}
          onSave={() => {
            postComment();
          }}
          toolbarFixed={false}
          styles={styles}
        />
      </div>

      {props.comments.map((item) => {
        return (
          <Comment
            author={item.author}
            date={item.updatedAt}
            commentText={item.content}
          />
        );
      })}
    </div>
  );
};

export default CommentBar;
