import React, { useState, useRef, forwardRef } from "react";
import { Editor, getEventTransfer } from "slate-react";
import { Block } from "slate";
import styles from "./TextEditor.module.css";
import FormatToolbar from "./FormatToolbar";
import { isKeyHotkey } from "is-hotkey";
import imageExtensions from "image-extensions";
import isUrl from "is-url";


const schema = {
  document: {
    last: { type: "paragraph" },
    normalize: (editor, { code, node, child }) => {
      switch (code) {
        case "last_child_type_invalid": {
          const paragraph = Block.create("paragraph");
          return editor.insertNodeByKey(node.key, node.nodes.size, paragraph);
        }
      }
    },
  },
  blocks: {
    image: {
      isVoid: true,
    },
  },
};



const TextEditor = forwardRef((props, ref) => {
  const editorRef = useRef();
  const value = props.value;
  const setValue = props.setValue;
  const DEFAULT_NODE = "paragraph";
  const isBoldHotkey = isKeyHotkey("mod+b");
  const isItalicHotkey = isKeyHotkey("mod+i");
  const isUnderlinedHotkey = isKeyHotkey("mod+u");
  const isCodeHotkey = isKeyHotkey("mod+`");
  const isTabHotkey = isKeyHotkey("tab");

  const hasMark = (type) => {
    return value.activeMarks.some((mark) => mark.type === type);
  };

  const hasBlock = (type) => {
    return value.blocks.some((node) => node.type === type);
  };

  const isImage = (url) => {
    return imageExtensions.includes(getExtension(url));
  };

  const getExtension = (url) => {
    return new URL(url).pathname.split(".").pop;
  };

  const insertImage = (editor, src, target) => {
    if (target) {
      editor.select(target);
    }

    editor.insertBlock({
      type: "image",
      data: { src },
    });
  };

  const insertFigure = (editor, src, target) => {
    if (target) {
      editor.select(target);
    }

    editor.insertBlock({
      type: "figure",
      data: { src },
    });
  };





  const renderBlock = (props, editor, next) => {
    const { attributes, children, node } = props;

    switch (node.type) {
      case "paragraph":
        return (
          <div className={styles["main-pane-item"]}>
            <p>{children}</p>
          </div>
        );
      case "block-quote":
        return (
          <div className={styles["main-pane-item"]}>
            <blockquote>{children}</blockquote>
          </div>
        );
      case "bulleted-list":
        return (
          <div className={styles["main-pane-item"]}>
            <ul>{children}</ul>
          </div>
        );
      case "heading-one":
        return (
          <div className={styles["main-pane-item"]}>
            <h1 className={styles["heading"]}>{children}</h1>
          </div>
        );
      case "heading-two":
        return (
          <div className={styles["main-pane-item"]}>
            <h2>{children}</h2>
          </div>
        );
      case "list-item":
        return (
            <li>{children}</li>
        );
      case "numbered-list":
        return (
          <div className={styles["main-pane-item"]}>
            <ol>{children}</ol>
          </div>
        );
      case "image": {
        const src = node.data.get("src");
        return (
          <div className={styles["main-pane-item"]}>
            <figure className={styles["image-container"]}>
              <img src={src} loading="lazy" />
            </figure>
          </div>
        );
      }
      case "figure": {
        const src = node.data.get("src");
        return (
          <div className={styles["main-pane-item"]}>
            <figure className={styles["image-container"]}>
              <img src={src} loading="lazy"/>
              <figcaption>
                <strong>{children}</strong>
              </figcaption>
            </figure>
          </div>
        );
      }
      default:
        return next();
    }
  };

  const renderMark = (props, editor, next) => {
    const { children, mark, attributes } = props;

    switch (mark.type) {
      case "bold":
        return <strong {...attributes}>{children}</strong>;
      case "code":
        return <code {...attributes}>{children}</code>;
      case "italic":
        return <em {...attributes}>{children}</em>;
      case "underlined":
        return <u {...attributes}>{children}</u>;
      default:
        return next();
    }
  };

  const onKeyDown = (event, editor, next) => {
    let mark;

    if (isBoldHotkey(event)) {
      mark = "bold";
    } else if (isItalicHotkey(event)) {
      mark = "italic";
    } else if (isUnderlinedHotkey(event)) {
      mark = "underlined";
    } else if (isCodeHotkey(event)) {
      mark = "code";
    } else if (isTabHotkey(event)) {
      event.preventDefault();
      return next();
    } else {
      return next();
    }

    event.preventDefault();
    editor.toggleMark(mark);
  };

  const onClickMark = (event, type) => {
    event.preventDefault();
    editorRef.current.toggleMark(type);
  };

  const onClickImage = (event) => {
    event.preventDefault();
    const src = window.prompt("enter the URL of the image:");
    // const caption = window.prompt("enter a caption for the image")
    if (!src) return;
    editorRef.current.command(insertImage, src);
  };

  const onClickFigure = (event) => {
    event.preventDefault();
    const src = window.prompt("enter the URL of the image:");
    if (!src) return;
    editorRef.current.command(insertFigure, src);
  };

  const onClickBlock = (event, type) => {
    event.preventDefault();

    const editor = editorRef.current;
    const value = editor.value;
    const document = value.document;

    // Handle everything but list buttons.
    if (type !== "bulleted-list" && type !== "numbered-list") {
      const isActive = hasBlock(type);
      const isList = hasBlock("list-item");

      if (isList) {
        editor
          .setBlocks(isActive ? DEFAULT_NODE : type)
          .unwrapBlock("bulleted-list")
          .unwrapBlock("numbered-list");
      } else {
        editor.setBlocks(isActive ? DEFAULT_NODE : type);
      }
    } else {
      // Handle the extra wrapping required for list buttons.
      const isList = hasBlock("list-item");
      const isType = value.blocks.some((block) => {
        return !!document.getClosest(
          block.key,
          (parent) => parent.type === type
        );
      });

      if (isList && isType) {
        editor
          .setBlocks(DEFAULT_NODE)
          .unwrapBlock("bulleted-list")
          .unwrapBlock("numbered-list");
      } else if (isList) {
        editor
          .unwrapBlock(
            type === "bulleted-list" ? "numbered-list" : "bulleted-list"
          )
          .wrapBlock(type);
      } else {
        editor.setBlocks("list-item").wrapBlock(type);
      }
    }
  };

  const onDropOrPaste = (event, editor, next) => {
    const target = editor.findEventRange(event);
    if (!target && event.type === "drop") return next();

    const transfer = getEventTransfer(event);
    const { type, text, files } = transfer;

    if (type === "files") {
      for (const file of files) {
        const reader = new FileReader();
        const [mime] = file.type.split("/");
        if (mime !== "image") continue;

        reader.addEventListener("load", () => {
          editor.command(insertImage, reader.result, target);
        });

        reader.readAsDataURL(file);
      }
      return;
    }

    if (type === "text") {
      if (!isUrl(text)) return next();
      if (!isImage(text)) return next();
      editor.command(insertImage, text, target);
      return;
    }

    next();
  };

  const renderOnceToolbar = (


    <FormatToolbar textEditorRef={ref}/>
  );

  return (
    <div className={styles["overarching"]}>
            {props.toolbarFixed ? (
            <div
              style={{ position: "fixed", top: "66px" }}
            >
              {/* <div style={{ position: "fixed", top: "103.5px", right: "0px"}}> */}
              {renderOnceToolbar}
            </div>
          ) : (
            <div>
              {renderOnceToolbar}
            </div>
          )}
      <Editor
        ref={editorRef}
        className={styles["Editor"]}
        value={value}
        onChange={(opts) => setValue(opts.value)}
        onKeyDown={onKeyDown}
        renderMark={renderMark}
        renderBlock={renderBlock}
        onDrop={onDropOrPaste}
        schema={schema}
      />
    </div>
  );
});

const BLOCK_TAGS = {
  p: "paragraph",
  blockquote: "block-quote",
  ul: `bulleted-list`,
  h1: `heading-one`,
  h2: `heading-two`,
  li: `list-item`,
  ol: `numbered-list`,
  img: `image`,
};

// Add a dictionary of mark tags.
const MARK_TAGS = {
  em: "italic",
  strong: "bold",
  u: "underlined",
  code: `code`,
};

export const rules = [
  {
    deserialize(el, next) {
      const type = BLOCK_TAGS[el.tagName.toLowerCase()];
      if (type) {
        return {
          object: "block",
          type: type,
          data: {
            className: el.getAttribute("class"),
          },
          nodes: next(el.childNodes),
        };
      }
    },
    serialize(obj, children) {
      if (obj.object == "block") {
        switch (obj.type) {
          case "paragraph":
            return (
              <di
                className={styles["main-pane"]}
                v
                className={styles["main-pane-item"]}
              >
                <p>{children}</p>
              </di>
            );
          case "block-quote":
            return (
              <div className={`${styles["main-pane-item"]}`}>
                <blockquote>{children}</blockquote>
              </div>
            );
          case "bulleted-list":
            return (
              <div className={styles["main-pane-item"]}>
                <ul>{children}</ul>
              </div>
            );
          case "heading-one":
            return (
              <div className={styles["main-pane-item"]}>
                <h1 className={styles["heading"]}>{children}</h1>
              </div>
            );
          case "heading-two":
            return (
              <div className={styles["main-pane-item"]}>
                <h2>{children}</h2>
              </div>
            );
          case "list-item":
            return <li>{children}</li>;
          case "numbered-list":
            return (
              <div className={styles["main-pane-item"]}>
                <ol>{children}</ol>
              </div>
            );
          case "image": {
            const src = obj.data["src"];
            return (
              <div className={`${styles["main-pane-item"]}`}>
                <figure className={styles["image-container"]}>
                  <img src={src} loading="lazy"/>
                </figure>
              </div>
            );
          }
          case "figure": {
            const src = obj.data["src"];
            return (
              <div className={`${styles["main-pane-item"]}`}>
                <figure className={styles["image-container"]}>
                  <img src={src} loading="lazy"/>
                  <figcaption>
                    <strong>{children}</strong>
                  </figcaption>
                </figure>
              </div>
            );
          }
        }
      }
    },
  },
  // Add a new rule that handles marks...
  {
    deserialize(el, next) {
      const type = MARK_TAGS[el.tagName.toLowerCase()];
      if (type) {
        return {
          object: "mark",
          type: type,
          nodes: next(el.childNodes),
        };
      }
    },
    serialize(obj, children) {
      if (obj.object == "mark") {
        switch (obj.type) {
          case "bold":
            return <strong>{children}</strong>;
          case "italic":
            return <em>{children}</em>;
          case "code":
            return <code>{children}</code>;
          case "underlined":
            return <u>{children}</u>;
        }
      }
    },
  },
];


export default TextEditor;
