import React from "react";
import LargeCard from "../../components/Cards/LargeCard/LargeCard.js";
import MediumCard from "../../components/Cards/MediumCard/MediumCard.js";
import Page from "../../components/Page/Page";
import FormatToolbar from "../../components/TextEditor/FormatToolbar"
import styles from "../../components/Page/Page.module.css";
import navbarStyles from "../../components/Navbar/Navbar.module.css"
import { useSelector } from "react-redux";

const Home = (props) => {
  const articles = useSelector((state) => state.blog.articles);
  const screen = useSelector((state) => state.device.screen);

  // const renderOnceToolbar = (
  //   <FormatToolbar>
  //     <div
  //       onPointerDown={() => {}}
  //     >
  //       {/* <i className={`${styles["fas"]} ${styles["fa-bold"]} fas fa-bold`} /> */}
  //     </div>
  //   </FormatToolbar>
  // );

  const toolbar = (
    <div

    className={navbarStyles["navbar-title-logo"]}
    onClick={() => {
      // if (props.sidebarClicked) {
      //   props.setSidebarClicked(false);
      // }
      // props.setNavbarClicked(false);
      // history.push("/");
    }}
  >

    <label className={`${styles["submit-button"]}`} >CREATE NEW ARTICLE</label>
  </div>
  )


  const list = 
  articles.map((item, index) =>
    index % 4 == 0 || screen === "mobile" ? (
      <div
        key={index}
        className={`${styles["main-pane-item"]} ${styles["main-pane-item-centered"]}`}
      >
        <LargeCard
          image={item.image}
          date={item.date}
          title={item.title}
          text={item.description}
          author={item.author}
          contentType={item.contentType}
          link={item.link}
        />
      </div>
    ) : (
      <div className={`${styles["main-pane-item"]}`}>
        <MediumCard
          image={item.image}
          date={item.date}
          title={item.title}
          text={item.description}
          author={item.author}
          contentType={item.contentType}
          link={item.link}
        />
      </div>
    )
  );

  const contents = (
    <>
    {toolbar}
    {list}
    </>
  )
  return <Page sidebarFixTopOffset={0} mainPane={contents} />;
};

export default Home;
